import React from "react";
import { useState } from 'react';

function Main_Form() {
  const [formstep, setFormstep] = useState(0);
  const NextStep = () => {
    setFormstep(formstep+1);
  }
  return (
    <form>
      {
        formstep === 0 && (
          <section>
            <label>Full name</label>
            <input type="text" placeholder="John Jonathan Doel" name="FullName"></input>
            <button onClick={NextStep}>Next</button>
          </section>
        )
      }
      {
        formstep === 1 && (
          <section>
            <label>Preffered Name</label>
            <input type="text" placeholder="John Jonathan" name="FullName"></input>
            <button onClick={NextStep}>Next</button>
          </section>
        )
      }
      {
        formstep === 2 && (
          <section>
            <label>Date of Birth</label>
            <input type="Date" placeholder="DD/MM/Y" name="dob"></input>
            <button onClick={NextStep}>Next</button>
          </section>
        )
      }
      {
        formstep === 3 && (
          <section>
            <label>NIC number</label>
            <input type="text" placeholder="Full name" name="FullName"></input>
          </section>
        )
      }

    </form>
  )
}

export default Main_Form
