import React from "react";
import { useState } from 'react';
import Data from './Form_inputs';

function MainForm() {

  const [formstep, setFormstep] = useState(0);

  const [data, setData] = useState({
    name: '',
    fullName:'',
    dob: '',
    nic: ''
  });

  const handleChange = (event) => {
    const name = event.target.name;
    const fullName = event.target.fullName;
    // const name = event.target.name;
    // const name = event.target.name;
    const value = event.target.value;
    setData(values => ({ [name]: value, [fullName]: value }));
  }

  // const [FullName, Name, DOB, NIC] = this.state(
  //   {
  //     FullName: '',
  //     Name: '',
  //     DOB: '',
  //     NIC: ''
  //   }
  // )

  const NextStep = () => {
    setFormstep(cur => cur + 1);
  }

  const PreviousStep = () => {
    setFormstep(cur => cur - 1);
  }

  const FormButton = () => {
    if (formstep === 0) {
      return (
        <button type="button" onClick={NextStep}>Next Step</button>
      )
    } else if (formstep > 0) {
      return (
        <div className="row">
          <button type="button" onClick={PreviousStep}>Back</button>
          <button type="button" onClick={NextStep}>Next Step</button>
        </div>
      )
    } else {
      return (
        <button type="submit">Create CV</button>
      )
    }
  }

  const InputType = () => {
    if (formstep < 4) {
      return (
        <h1>Personal Details</h1>
      )
    } else {
      return (
        <h1>Acodemic Information</h1>
      )
    }
  }

  // const CalculateAge = () => {
  //   const age =0;
  // }

  return (
    <form>
      {InputType()}
      {data.name}
      {data.fullName}
      {
        formstep === 0 && (
          <section>
            <label>Name</label>
            <input type="text" value={data.name || ""} onChange={handleChange} placeholder="John Jonathan Doel" name="name"></input>
          </section>
        )
      }
      {
        formstep === 1 && (
          <section>
            <label>Full Name</label>
            <input type="text" value={data.name || ""} onChange={handleChange} placeholder="John Jonathan Doel" name="name"></input>
          </section>
        )
      }
      {
        formstep === 2 && (
          <section>
            <label>Date of Birth</label>            
            <input type="text" value={data.name || ""} onChange={handleChange} placeholder="John Jonathan Doel" name="name"></input>
          </section>
        )
      }
      {
        formstep === 3 && (
          <section>
            <label>NIC number</label>
            <input type="text" value={data.name || ""} onChange={handleChange} placeholder="John Jonathan Doel" name="name"></input>
          </section>
        )
      }
      {
        formstep === 4 && (
          <section>
            <label>Schools and/or Universities </label>
            <input type="text" value={data.name || ""} onChange={handleChange} placeholder="John Jonathan Doel" name="name"></input>
          </section>
        )
      }
      {FormButton()}
    </form>
  )
}

export default MainForm
