import React from "react";
import { useState } from 'react';

function MainForm() {

  const [formstep, setFormstep] = useState(0);
  const NextStep = () => {
    setFormstep(cur => cur + 1);
  }

  const Backward = () => {
    if (formstep = 0) {
      return (
        <button type="button" onClick={NextStep}>Back</button>
      )
    }
  }

  return (
    <form>
      {
        formstep === 0 && (
          <section>
            <label>Full name</label>
            <input type="text" placeholder="John Jonathan Doel" name="FullName"></input>
          </section>
        )
      }
      {
        formstep === 1 && (
          <section>
            <label>Preffered Name</label>
            <input type="text" placeholder="John Jonathan" name="FullName"></input>
          </section>
        )
      }
      {
        formstep === 2 && (
          <section>
            <label>Date of Birth</label>
            <input type="Date" placeholder="DD/MM/Y" id="dob" name="dob"></input>
          </section>
        )
      }
      {
        formstep === 3 && (
          <section>
            <label>NIC number</label>
            <input type="text" placeholder="Full name" name="FullName"></input>
          </section>

        )
      }
      {Backward}
      <button type="button" onClick={NextStep}>Next</button>

    </form>
  )
}

export default MainForm
