import React, { Component } from "react";
import 'react-bootstrap';

export default class Form extends Component {
  state = {
    step: 1,
    Fullname: '',
    PrefName: '',
    DOB: ''
  }
  // go back to previous step
  prevStep = () => {
    const { step } = this.state;
    this.setState({ step: step - 1 });
  }
  // proceed to the next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({ step: step + 1 });
  }
  // handle field change
