import React from "react";
import { useState } from 'react';
import Data from './Form_inputs';

function MainForm() {

  const [formstep, setFormstep] = useState(0);

  const [data, setData] = useState({});

  const handleChange = (event) => {
    const name = event.target.name;
    setData(value => ([name]: value));
  }

  // const [FullName, Name, DOB, NIC] = this.state(
  //   {
  //     FullName: '',
  //     Name: '',
  //     DOB: '',
  //     NIC: ''
  //   }
  // )

  const NextStep = () => {
    setFormstep(cur => cur + 1);
  }

  const PreviousStep = () => {
    setFormstep(cur => cur - 1);
  }

  const FormButton = () => {
    if (formstep === 0) {
      return (
        <button type="button" onClick={NextStep}>Next Step</button>
      )
    } else if (formstep > 0) {
      return (
        <div className="row">
          <button type="button" onClick={PreviousStep}>Back</button>
          <button type="button" onClick={NextStep}>Next Step</button>
        </div>
      )
    } else {
      return (
        <button type="submit">Create CV</button>
      )
    }
  }

  const InputType = () => {
    if (formstep < 4) {
      return (
        <h1>Personal Details</h1>
      )
    } else {
      return (
        <h1>Acodemic Information</h1>
      )
    }
  }

  // const CalculateAge = () => {
  //   const age =0;
  // }

  return (
    <form>
      {InputType()}
      {
        formstep === 0 && (
          <section>
            <label>Full name</label>
            <input type="text" value={data.name || ""} onChange={handleChange} placeholder="John Jonathan Doel" name="FullName"></input>
          </section>
        )
      }
      {
        formstep === 1 && (
          <section>
            <label>Preffered Name</label>
            <input type="text" placeholder="John Jonathan" name="FullName"></input>
          </section>
        )
      }
      {
        formstep === 2 && (
          <section>
            <label>Date of Birth</label>
            <input type="Date" placeholder="DD/MM/Y" id="dob" name="dob"></input>

          </section>
        )
      }
      {
        formstep === 3 && (
          <section>
            <label>NIC number</label>
            <input type="text" placeholder="Full name" name="FullName"></input>
          </section>
        )
      }
      {
        formstep === 4 && (
          <section>
            <label>Schools and/or Universities </label>
            <input type="text" placeholder="Full name" name="FullName"></input>
          </section>
        )
      }
      {FormButton()}
    </form>
  )
}

export default MainForm
